模拟时间片轮转调度算法，并计算每个进程的完成时间、周转时间和等待时间。 #include <stdio.h> typedef struct { int pid; // 进程ID int arrival_time; // 到达时间 int burst_time; // 运行时间 int remaining_time; // 剩余运行时间 int completion_time; // 完成时间 int turnaround_time; // 周转时间 int waiting_time; // 等待时间
} Process; #define TIME_SLICE 4
#define NUM_PROCESSES 4 void calculateTime(Process processes[], int n) { int time = 0; int completed = 0; while (completed < n) { for (int i = 0; i < n; i++) { if (processes[i].arrival_time <= time && processes[i].remaining_time > 0) { if (processes[i].remaining_time <= TIME_SLICE) { time += processes[i].remaining_time; processes[i].remaining_time = 0; processes[i].completion_time = time; processes[i].turnaround_time = processes[i].completion_time - processes[i].arrival_time; processes[i].waiting_time = processes[i].turnaround_time - processes[i].burst_time; completed++; } else { time += TIME_SLICE; processes[i].remaining_time -= TIME_SLICE; } } } }
} int main() { Process processes[NUM_PROCESSES] = { {1, 0, 10, 10, 0, 0, 0}, {2, 1, 6, 6, 0, 0, 0}, {3, 2, 8, 8, 0, 0, 0}, {4, 3, 4, 4, 0, 0, 0} }; calculateTime(processes, NUM_PROCESSES); for (int i = 0; i < NUM_PROCESSES; i++) { printf("P%d 完成时间: %d, 周转时间: %d, 等待时间: %d\n", processes[i].pid, processes[i].completion_time, processes[i].turnaround_time, processes[i].waiting_time); } return 0;
}

